// src/pages/Checkout.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

export default function Checkout({ cart, user, setCart }) {
  const navigate = useNavigate();
  const [address, setAddress] = useState("");
  const [paymentMode, setPaymentMode] = useState("cod"); // default: Cash on Delivery

  const totalPrice = cart.reduce((total, pizza) => {
    const price = pizza.price || 0;
    return total + price * pizza.quantity;
  }, 0);

  const handlePlaceOrder = () => {
    if (!user) {
      toast.error("You must be logged in to place an order.");
      navigate("/login");
      return;
    }

    if (cart.length === 0) {
      toast.error("Your cart is empty.");
      return;
    }

    if (!address.trim()) {
      toast.error("Please enter a delivery address.");
      return;
    }

    const orderDetails = {
      userId: user.id,
      pizzas: cart,
      totalAmount: totalPrice,
      address: address.trim(),
      paymentMode,
      orderId: Date.now(),
      status: "Placed",
    };

    console.log("Order placed:", orderDetails); // Optional: check order in console

    toast.success("Order placed successfully! ✅");

    // Clear cart after placing order
    setCart([]);

    // Redirect to home
    navigate("/");
  };

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h1>Checkout</h1>

      <h2>Order Summary</h2>
      {cart.map((item, index) => (
        <div
          key={item.cartId || index}
          style={{
            display: "flex",
            justifyContent: "space-between",
            borderBottom: "1px solid #eee",
            padding: "10px 0",
          }}
        >
          <span>
            {item.name} ({item.size || "Custom"})
            {item.quantity > 1 ? ` (x${item.quantity})` : ""}
          </span>
          <span>₹{((item.price || 0) * item.quantity).toFixed(2)}</span>
        </div>
      ))}

      <h3 style={{ marginTop: "1rem" }}>Delivery Address</h3>
      <textarea
        value={address}
        onChange={(e) => setAddress(e.target.value)}
        placeholder="Enter your delivery address"
        style={{ width: "100%", padding: "10px", marginTop: "0.5rem" }}
        rows={3}
      />

      <h3 style={{ marginTop: "1rem" }}>Select Payment Mode</h3>
      <select
        value={paymentMode}
        onChange={(e) => setPaymentMode(e.target.value)}
        style={{ width: "100%", padding: "10px", marginTop: "0.5rem" }}
      >
        <option value="cod">Cash on Delivery</option>
        <option value="upi">UPI</option>
        <option value="gpay">Google Pay</option>
        <option value="card">Debit/Credit Card</option>
      </select>

      <h3 style={{ textAlign: "right", marginTop: "1rem" }}>
        Total: ₹{totalPrice.toFixed(2)}
      </h3>

      <button
        onClick={handlePlaceOrder}
        style={{
          marginTop: "1rem",
          width: "100%",
          padding: "15px",
          backgroundColor: "#007bff",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
          fontSize: "16px",
        }}
      >
        Proceed to Payment
      </button>
    </div>
  );
}
