// src/pages/CartPage.js
import React from "react";
import { useNavigate } from "react-router-dom";

export default function CartPage({ cart, setCart }) {
  const navigate = useNavigate();

  const handleRemove = (cartId) => {
    setCart(cart.filter((item) => (item.cartId || item._id) !== cartId));
  };

  const totalPrice = cart.reduce((total, item) => {
    return total + (item.price || 0) * item.quantity;
  }, 0);

  if (cart.length === 0) {
    return (
      <div style={{ padding: "20px", textAlign: "center" }}>
        <h2>Your Cart ðŸ›’</h2>
        <p>No items in your cart yet.</p>
      </div>
    );
  }

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2>Your Cart ðŸ›’</h2>
      {cart.map((item, index) => (
        <div
          key={item.cartId || item._id || index}
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            borderBottom: "1px solid #eee",
            padding: "10px 0",
          }}
        >
          <span>
            {item.name} ({item.size || "Custom"})
            {item.quantity > 1 ? ` (x${item.quantity})` : ""}
          </span>
          <span>â‚¹{((item.price || 0) * item.quantity).toFixed(2)}</span>
          <button
            onClick={() => handleRemove(item.cartId || item._id)}
            style={{
              marginLeft: "10px",
              padding: "5px 10px",
              backgroundColor: "#dc3545",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Remove
          </button>
        </div>
      ))}
      <h3 style={{ textAlign: "right", marginTop: "1rem" }}>
        Total: â‚¹{totalPrice.toFixed(2)}
      </h3>
      <button
        onClick={() => navigate("/checkout")}
        style={{
          marginTop: "1rem",
          width: "100%",
          padding: "15px",
          backgroundColor: "#007bff",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
          fontSize: "16px",
        }}
      >
        Proceed to Checkout
      </button>
    </div>
  );
}
