import React, { useState, useEffect } from 'react';
import api from '../api';
import Pizza from '../components/Pizza';
import Pagination from '../components/Pagination';

export default function Home({ addToCart }) {
  const [pizzas, setPizzas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // State for filtering and pagination
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('all');
  const [page, setPage] = useState(1);
  const [pages, setPages] = useState(1);

  useEffect(() => {
    let isMounted = true;
    const fetchPizzas = async () => {
      try {
        setLoading(true);
        const { data } = await api.get(`/pizzas?page=${page}&search=${searchTerm}&category=${category}`);
        if (isMounted) {
          setPizzas(data.pizzas);
          setPages(data.pages);
        }
      } catch (err) {
        if (isMounted) {
          console.error("Failed to fetch pizzas:", err);
          setError('Could not load pizzas. Please make sure the backend server is running.');
        }
      } finally {
        if (isMounted) {
          setLoading(false);
        }
      }
    };

    fetchPizzas();

    return () => {
      isMounted = false;
    };
  }, [page, searchTerm, category]);

  if (loading) return <div>Loading menu...</div>;
  if (error) return <div style={{ color: 'red', padding: '20px' }}>{error}</div>;

  // Hardcoded categories. In a larger app, this could be fetched from its own API endpoint.
  const categories = ['all', 'veg', 'non-veg', 'classic', 'specialty'];

  const handleFilterChange = (setter) => (e) => {
    setter(e.target.value);
    setPage(1); // Reset to the first page whenever a filter changes
  };

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'center', gap: '2rem', marginBottom: '2rem', alignItems: 'center', flexWrap: 'wrap' }}>
        <div>
          <label htmlFor="search" style={{ marginRight: '0.5rem' }}>Search Pizzas:</label>
          <input
            id="search"
            type="text"
            placeholder="e.g., Margherita"
            value={searchTerm}
            onChange={handleFilterChange(setSearchTerm)}
            style={{ padding: '8px' }}
          />
        </div>
        <div>
          <label htmlFor="category" style={{ marginRight: '0.5rem' }}>Filter by Category:</label>
          <select
            id="category"
            value={category}
            onChange={handleFilterChange(setCategory)}
            style={{ padding: '8px', textTransform: 'capitalize' }}
          >
            {categories.map(cat => (
              <option key={cat} value={cat} style={{ textTransform: 'capitalize' }}>
                {cat}
              </option>
            ))}
          </select>
        </div>
      </div>

      <h2 style={{ textAlign: 'center' }}>Pizza Menu</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', justifyContent: 'center' }}>
        {pizzas.length > 0 ? (
          pizzas.map(pizza => (
            <Pizza key={pizza._id} pizza={pizza} addToCart={addToCart} />
          ))
        ) : (
          <p>No pizzas match your criteria. Try adjusting your search or filters.</p>
        )}
      </div>
      <Pagination pages={pages} page={page} setPage={setPage} />
    </div>
  );
}
