// src/pages/CartPage.js
import React from "react";
import { Link } from "react-router-dom";

export default function CartPage({ cart, setCart }) {
  const removeFromCart = (id, isCustom) => {
    setCart((prevCart) =>
      prevCart.filter((item) =>
        isCustom ? item.cartId !== id : item._id !== id
      )
    );
  };

  if (!cart || cart.length === 0) {
    return (
      <div style={{ textAlign: "center", marginTop: "2rem" }}>
        <h2>Your Cart ðŸ›’</h2>
        <p>No items in your cart yet.</p>
        <Link to="/">Go back to menu</Link>
      </div>
    );
  }

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2>Your Cart ðŸ›’</h2>
      {cart.map((item) => (
        <div
          key={item.cartId || item._id}
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            borderBottom: "1px solid #eee",
            padding: "10px 0",
          }}
        >
          <span>
            {item.name} (x{item.quantity})
          </span>
          <span>â‚¹{(item.price * item.quantity).toFixed(2)}</span>
          <button
            onClick={() =>
              removeFromCart(item.isCustom ? item.cartId : item._id, item.isCustom)
            }
            style={{
              background: "red",
              color: "white",
              border: "none",
              padding: "5px 10px",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Remove
          </button>
        </div>
      ))}

      <h3 style={{ textAlign: "right", marginTop: "1rem" }}>
        Total: â‚¹
        {cart.reduce((sum, item) => sum + item.price * item.quantity, 0).toFixed(2)}
      </h3>

      {/* Checkout button */}
      <div style={{ textAlign: "right", marginTop: "1rem" }}>
        <Link to="/checkout">
          <button
            style={{
              background: "green",
              color: "white",
              padding: "10px 20px",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Proceed to Checkout
          </button>
        </Link>
      </div>
    </div>
  );
}
