import React from 'react';

const Pagination = ({ pages, page, setPage }) => {
  if (pages <= 1) {
    return null;
  }

  return (
    <div style={{ display: 'flex', justifyContent: 'center', marginTop: '2rem', paddingBottom: '2rem' }}>
      {[...Array(pages).keys()].map((x) => (
        <button
          key={x + 1}
          onClick={() => setPage(x + 1)}
          disabled={page === x + 1}
          style={{
            padding: '10px 15px',
            margin: '0 5px',
            cursor: 'pointer',
            backgroundColor: page === x + 1 ? '#007bff' : 'white',
            color: page === x + 1 ? 'white' : 'black',
            border: '1px solid #ddd',
            fontWeight: page === x + 1 ? 'bold' : 'normal',
          }}
        >
          {x + 1}
        </button>
      ))}
    </div>
  );
};

export default Pagination;
