import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Pizza from './components/Pizza';
import Cart from './components/Cart'; // We‚Äôll create this next

function App() {
  const [pizzas, setPizzas] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/pizzas')
      .then(res => setPizzas(res.data))
      .catch(err => console.error(err));
  }, []);

 const addToCart = (pizza) => {
  const existingPizzaIndex = cart.findIndex(p => p._id === pizza._id);
  const updatedCart = [...cart];

  if (existingPizzaIndex !== -1) {
    updatedCart[existingPizzaIndex].quantity += 1;
  } else {
    updatedCart.push({ ...pizza, quantity: 1 });
  }

  setCart(updatedCart);
};

  return (
    <Router>
      <div style={{ padding: '1rem' }}>
        <nav>
          <Link to="/">üè† Home</Link> |{' '}
          <Link to="/cart">üõí Cart ({cart.length})</Link>
        </nav>

        <Routes>
          <Route
            path="/"
            element={
              <div>
                <h1>üçï Pizza Menu</h1>
                {pizzas.map((pizza, index) => (
                  <Pizza key={index} pizza={pizza} addToCart={addToCart} />
                ))}
              </div>
            }
          />
          <Route
  path="/cart"
  element={<Cart cart={cart} setCart={setCart} />}
/>

        </Routes>
      </div>
    </Router>
  );
}

export default App;
