import React from 'react';

function Pizza({ pizza, addToCart }) {
  return (
    <div style={{
      border: '1px solid #ccc',
      borderRadius: '8px',
      padding: '1rem',
      marginBottom: '1rem',
      maxWidth: '400px'
    }}>
      <h2>üçï {pizza.name}</h2>
      <img src={pizza.image} alt={pizza.name} width="200" />
      <p>{pizza.description}</p>
      <p><strong>Category:</strong> {pizza.category}</p>
      <p><strong>Prices:</strong></p>
      <ul>
        <li>Small: ‚Çπ{pizza.prices.small}</li>
        <li>Medium: ‚Çπ{pizza.prices.medium}</li>
        <li>Large: ‚Çπ{pizza.prices.large}</li>
      </ul>
      <button onClick={() => addToCart(pizza)}>‚ûï Add to Cart</button>
    </div>
  );
}

export default Pizza;
