import React, { useState } from "react";
import { Link, NavLink } from "react-router-dom";
import { Menu, X } from "lucide-react";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="bg-gradient-to-r from-red-500 to-red-600 px-6 py-4 shadow-lg sticky top-0 z-50">
      <div className="flex justify-between items-center max-w-7xl mx-auto">
        {/* Logo */}
        <Link to="/" className="text-white text-2xl font-extrabold tracking-wide flex items-center gap-2">
          üçï Pizza Delivery
        </Link>

        {/* Desktop Menu */}
        <div className="hidden md:flex items-center space-x-8">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `text-white font-medium hover:text-yellow-300 transition ${
                isActive ? "border-b-2 border-yellow-400 pb-1" : ""
              }`
            }
          >
            Home
          </NavLink>

          <NavLink
            to="/cart"
            className={({ isActive }) =>
              `text-white font-medium hover:text-yellow-300 transition ${
                isActive ? "border-b-2 border-yellow-400 pb-1" : ""
              }`
            }
          >
            Cart
          </NavLink>

          <NavLink
            to="/custom-pizza"
            className={({ isActive }) =>
              `text-white font-medium hover:text-yellow-300 transition ${
                isActive ? "border-b-2 border-yellow-400 pb-1" : ""
              }`
            }
          >
            Custom Pizza
          </NavLink>

          <Link
            to="/login"
            className="bg-white text-red-600 px-5 py-2 rounded-xl font-semibold shadow hover:bg-gray-100 transition"
          >
            Login
          </Link>
          <Link
            to="/register"
            className="bg-yellow-400 text-black px-5 py-2 rounded-xl font-semibold shadow hover:bg-yellow-300 transition"
          >
            Register
          </Link>
        </div>

        {/* Mobile Hamburger */}
        <button
          className="md:hidden text-white"
          onClick={() => setIsOpen(!isOpen)}
        >
          {isOpen ? <X size={28} /> : <Menu size={28} />}
        </button>
      </div>

      {/* Mobile Menu */}
      {isOpen && (
        <div className="md:hidden mt-4 flex flex-col space-y-4 bg-red-600 rounded-lg p-4">
          <NavLink
            to="/"
            onClick={() => setIsOpen(false)}
            className="text-white font-medium hover:text-yellow-300"
          >
            Home
          </NavLink>
          <NavLink
            to="/cart"
            onClick={() => setIsOpen(false)}
            className="text-white font-medium hover:text-yellow-300"
          >
            Cart
          </NavLink>
          <NavLink
            to="/custom-pizza"
            onClick={() => setIsOpen(false)}
            className="text-white font-medium hover:text-yellow-300"
          >
            Custom Pizza
          </NavLink>
          <Link
            to="/login"
            onClick={() => setIsOpen(false)}
            className="bg-white text-red-600 px-4 py-2 rounded-lg font-semibold hover:bg-gray-100"
          >
            Login
          </Link>
          <Link
            to="/register"
            onClick={() => setIsOpen(false)}
            className="bg-yellow-400 text-black px-4 py-2 rounded-lg font-semibold hover:bg-yellow-300"
          >
            Register
          </Link>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
