import React, { useState, useEffect } from 'react';
import api from '../api';
import { toast } from 'react-toastify';

const AdminDashboard = ({ user }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchAllOrders = async () => {
    setLoading(true);
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      };
      const { data } = await api.get('/orders', config);
      setOrders(data);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Could not fetch orders.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (user && user.user.role === 'admin') {
      fetchAllOrders();
    }
  }, [user]);

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      const config = {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${user.token}`,
        },
      };
      await api.put(
        `/orders/${orderId}/status`,
        { status: newStatus },
        config
      );
      toast.success(`Order status updated to ${newStatus}`);
      fetchAllOrders(); // Refresh the list
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to update status.');
    }
  };

  if (loading) return <div>Loading all orders...</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h1>Admin Dashboard - All Orders</h1>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ borderBottom: '2px solid black', textAlign: 'left' }}>
              <th style={{ padding: '8px' }}>Order ID</th>
              <th style={{ padding: '8px' }}>User</th>
              <th style={{ padding: '8px' }}>Date</th>
              <th style={{ padding: '8px' }}>Total</th>
              <th style={{ padding: '8px' }}>Status</th>
              <th style={{ padding: '8px' }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order._id} style={{ borderBottom: '1px solid #ddd' }}>
                <td style={{ padding: '8px' }}>{order._id}</td>
                <td style={{ padding: '8px' }}>{order.userId?.name || 'N/A'}</td>
                <td style={{ padding: '8px' }}>{new Date(order.createdAt).toLocaleDateString()}</td>
                <td style={{ padding: '8px' }}>₹{order.totalAmount.toFixed(2)}</td>
                <td style={{ padding: '8px', textTransform: 'capitalize' }}>{order.status}</td>
                <td style={{ padding: '8px' }}>
                  <select defaultValue={order.status} onChange={(e) => handleStatusChange(order._id, e.target.value)}>
                    <option value="pending">Pending</option>
                    <option value="preparing">Preparing</option>
                    <option value="out_for_delivery">Out for Delivery</option>
                    <option value="delivered">Delivered</option>
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AdminDashboard;
