import React, { useState } from 'react';

function Pizza({ pizza, addToCart }) {
  const [size, setSize] = useState('small');

  const handleAddToCart = () => {
    // Create a new object for the cart with selected size and price
    const itemToAdd = {
      ...pizza,
      name: `${pizza.name} (${size.charAt(0).toUpperCase() + size.slice(1)})`, // e.g., "Margherita (Medium)"
      size: size,
      price: pizza.prices[size], // Get price for the selected size
    };
    addToCart(itemToAdd);
  };

  return (
    <div style={{
      border: '1px solid #ccc',
      borderRadius: '8px',
      padding: '1rem',
      marginBottom: '1rem',
      maxWidth: '400px',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between'
    }}>
      <div>
        <h2>üçï {pizza.name}</h2>
        <img src={pizza.image} alt={pizza.name} width="200" />
        <p>{pizza.description}</p>
        <p><strong>Category:</strong> {pizza.category}</p>
        <div style={{ margin: '1rem 0' }}>
          <span>Size: </span>
          <select value={size} onChange={(e) => setSize(e.target.value)} style={{ marginLeft: '0.5rem', padding: '5px' }}>
            <option value="small">Small - ‚Çπ{pizza.prices.small}</option>
            <option value="medium">Medium - ‚Çπ{pizza.prices.medium}</option>
            <option value="large">Large - ‚Çπ{pizza.prices.large}</option>
          </select>
        </div>
      </div>
      <button onClick={handleAddToCart} style={{ marginTop: 'auto', padding: '10px', cursor: 'pointer' }}>‚ûï Add to Cart</button>
    </div>
  );
}

export default Pizza;
