// src/pages/Home.js
import React, { useState, useEffect } from 'react';
import api from '../api';
import Pizza from '../components/Pizza';
import Pagination from '../components/Pagination';

export default function Home({ addToCart }) {
  const [pizzas, setPizzas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Search and pagination states
  const [searchTerm, setSearchTerm] = useState('');
  const [page, setPage] = useState(1);
  const [pages, setPages] = useState(1);

  useEffect(() => {
    let isMounted = true;
    const fetchPizzas = async () => {
      try {
        setLoading(true);
        const { data } = await api.get(
          `/pizzas?page=${page}&search=${searchTerm}`
        );
        if (isMounted) {
          setPizzas(data.pizzas);
          setPages(data.pages);
        }
      } catch (err) {
        if (isMounted) {
          console.error("Failed to fetch pizzas:", err);
          setError('Could not load pizzas. Please make sure the backend server is running.');
        }
      } finally {
        if (isMounted) setLoading(false);
      }
    };

    fetchPizzas();
    return () => { isMounted = false; };
  }, [page, searchTerm]);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    setPage(1); // reset to first page on new search
  };

  if (loading) return <div>Loading menu...</div>;
  if (error) return <div style={{ color: 'red', padding: '20px' }}>{error}</div>;

  return (
    <div style={{ padding: '20px' }}>
      {/* Search bar at the very top */}
      <div style={{ marginBottom: '20px', textAlign: 'center' }}>
        <input
          type="text"
          placeholder="Search pizzas, e.g., Margherita"
          value={searchTerm}
          onChange={handleSearchChange}
          style={{
            padding: '10px',
            width: '400px',
            borderRadius: '5px',
            border: '1px solid #ccc',
            fontSize: '16px'
          }}
        />
      </div>

      <h2 style={{ textAlign: 'center' }}>Pizza Menu</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', justifyContent: 'center' }}>
        {pizzas.length > 0 ? (
          pizzas.map(pizza => (
            <Pizza key={pizza._id} pizza={pizza} addToCart={addToCart} />
          ))
        ) : (
          <p style={{ textAlign: 'center' }}>No pizzas match your search. Try a different name.</p>
        )}
      </div>

      <Pagination pages={pages} page={page} setPage={setPage} />
    </div>
  );
}
