// src/App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

// Pages
import Home from "./pages/Home";
import Cart from "./pages/CartPage";
import Login from "./pages/Login";
import Register from "./pages/Register";
import CustomPizza from "./pages/CustomPizza";
import Checkout from "./pages/Checkout";

// Components
import Navbar from "./components/Navbar";
import Chatbot from "./components/Chatbot";

function App() {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);

  // Load user from localStorage on page load
  useEffect(() => {
    const storedUser = localStorage.getItem("fakeUser");
    if (storedUser) setUser(JSON.parse(storedUser));
  }, []);

  // Save user to localStorage whenever it changes
  useEffect(() => {
    if (user) localStorage.setItem("fakeUser", JSON.stringify(user));
    else localStorage.removeItem("fakeUser");
  }, [user]);

  // üëâ Add normal pizza
  const addToCart = (pizza) => {
    setCart((prevCart) => {
      const existing = prevCart.find(
        (item) => item._id === pizza._id && !item.isCustom
      );
      if (existing) {
        toast.info(`${pizza.name} quantity updated üçï`);
        return prevCart.map((item) =>
          item._id === pizza._id && !item.isCustom
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      toast.success(`${pizza.name} added to cart üçï`);
      return [...prevCart, { ...pizza, quantity: 1, isCustom: false }];
    });
  };

  // üëâ Add custom pizza (fixed price ‚Çπ400)
  const addCustomPizzaToCart = (customPizza) => {
    setCart((prevCart) => [
      ...prevCart,
      {
        ...customPizza,
        price: 400,
        quantity: 1,
        isCustom: true,
        cartId: Date.now(),
      },
    ]);
    toast.success("Your custom pizza was added üçï‚ú®");
  };

  return (
    <Router>
      {/* Navbar */}
      <Navbar cart={cart} user={user} setUser={setUser} />

      {/* Main Content */}
      <div className="max-w-7xl mx-auto p-4">
        <Routes>
          <Route path="/" element={<Home addToCart={addToCart} />} />
          <Route path="/cart" element={<Cart cart={cart} setCart={setCart} />} />
          <Route path="/login" element={<Login setUser={setUser} />} />
          <Route path="/register" element={<Register setUser={setUser} />} />
          <Route
            path="/custom-pizza"
            element={<CustomPizza addCustomPizzaToCart={addCustomPizzaToCart} />}
          />
          <Route
            path="/checkout"
            element={<Checkout cart={cart} user={user} setCart={setCart} />}
          />
        </Routes>
      </div>

      {/* Chatbot always visible */}
      <Chatbot />

      {/* Toast notifications container */}
      <ToastContainer position="top-right" autoClose={2000} />
    </Router>
  );
}

export default App;
