import React from 'react';

const Cart = ({ cart, setCart }) => {
  const updateQuantity = (index, change) => {
    const updatedCart = [...cart];
    updatedCart[index].quantity += change;
    if (updatedCart[index].quantity <= 0) {
      updatedCart.splice(index, 1); // remove if quantity is 0
    }
    setCart(updatedCart);
  };

  const getTotalPrice = () => {
    return cart.reduce((total, pizza) => {
      return total + (pizza.prices.medium || 0) * pizza.quantity;
    }, 0);
  };

  return (
    <div>
      <h2>üõí Your Cart</h2>
      {cart.length === 0 ? (
        <p>No pizzas added yet.</p>
      ) : (
        <div>
          <ul>
            {cart.map((pizza, index) => (
              <li key={index}>
                üçï <strong>{pizza.name}</strong> - ‚Çπ{pizza.prices.medium} √ó {pizza.quantity}
                <button onClick={() => updateQuantity(index, 1)} style={{ marginLeft: '10px' }}>‚ûï</button>
                <button onClick={() => updateQuantity(index, -1)} style={{ marginLeft: '5px' }}>‚ûñ</button>
              </li>
            ))}
          </ul>
          <h3>Total: ‚Çπ{getTotalPrice()}</h3>
        </div>
      )}
    </div>
  );
};

export default Cart;
