// src/App.js
import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

// Pages
import Home from "./pages/Home";
import Cart from "./pages/CartPage";
import Login from "./pages/Login";
import Register from "./pages/Register";
import CustomPizza from "./pages/CustomPizza";

// Components
import Navbar from "./components/Navbar";
import Chatbot from "./components/Chatbot";

function App() {
  const [cart, setCart] = useState([]);

  // ðŸ‘‰ Add normal pizza
  const addToCart = (pizza) => {
    setCart((prevCart) => {
      const existing = prevCart.find((item) => item._id === pizza._id && !item.isCustom);
      if (existing) {
        return prevCart.map((item) =>
          item._id === pizza._id && !item.isCustom
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...prevCart, { ...pizza, quantity: 1, isCustom: false }];
    });
  };

  // ðŸ‘‰ Add custom pizza
  const addCustomPizzaToCart = (customPizza) => {
    setCart((prevCart) => [
      ...prevCart,
      { ...customPizza, quantity: 1, isCustom: true, cartId: Date.now() } // unique id
    ]);
  };

  return (
    <Router>
      {/* Navbar */}
      <Navbar cart={cart} />

      {/* Main Content */}
      <div className="max-w-7xl mx-auto p-4">
        <Routes>
          <Route path="/" element={<Home addToCart={addToCart} />} />
          <Route path="/cart" element={<Cart cart={cart} setCart={setCart} />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route
            path="/custom-pizza"
            element={<CustomPizza addCustomPizzaToCart={addCustomPizzaToCart} />}
          />
        </Routes>
      </div>

      {/* Chatbot always visible */}
      <Chatbot />
    </Router>
  );
}

export default App;
