// src/App.js
import React, { useState } from "react";
import Home from "./pages/Home";
import Cart from "./pages/CartPage"; // updated import
import Login from "./pages/Login";
import Register from "./pages/Register";
import CustomPizza from "./pages/CustomPizza";
import Chatbot from "./components/Chatbot"; // make sure you have this component

function App() {
  const [activePage, setActivePage] = useState("home"); // track which page to show

  const renderPage = () => {
    switch (activePage) {
      case "home":
        return <Home />;
      case "cart":
        return <Cart />;
      case "login":
        return <Login />;
      case "register":
        return <Register />;
      case "customPizza":
        return <CustomPizza />;
      default:
        return <Home />;
    }
  };

  return (
    <div>
      {/* Vertical Navbar */}
      <nav
        style={{
          display: "flex",
          flexDirection: "column",
          padding: "10px",
          gap: "10px",
          borderBottom: "1px solid #ccc",
        }}
      >
        <button onClick={() => setActivePage("home")}>Home</button>
        <button onClick={() => setActivePage("cart")}>Cart</button>
        <button onClick={() => setActivePage("login")}>Login</button>
        <button onClick={() => setActivePage("register")}>Register</button>
        <button onClick={() => setActivePage("customPizza")}>Custom Pizza</button>
      </nav>

      {/* Page Content */}
      <div style={{ padding: "20px" }}>{renderPage()}</div>

      {/* Chatbot always visible */}
      <Chatbot />
    </div>
  );
}

export default App;
