import React from 'react';

const Cart = ({ cart }) => {
  const getTotalPrice = () => {
    return cart.reduce((total, pizza) => {
      return total + (pizza.prices.medium || 0); // using medium as default price
    }, 0);
  };

  return (
    <div>
      <h2>üõí Your Cart</h2>
      {cart.length === 0 ? (
        <p>No pizzas added yet.</p>
      ) : (
        <div>
          <ul>
            {cart.map((pizza, index) => (
              <li key={index}>
                üçï <strong>{pizza.name}</strong> - ‚Çπ{pizza.prices.medium}
              </li>
            ))}
          </ul>
          <h3>Total: ‚Çπ{getTotalPrice()}</h3>
        </div>
      )}
    </div>
  );
};

export default Cart;
