// frontend/src/components/Cart.js
import React from 'react';

function Cart({ cart, setCart }) {
  const removeFromCart = (id) => {
    const updatedCart = cart.filter(pizza => pizza._id !== id);
    setCart(updatedCart);
  };

  const totalPrice = cart.reduce((total, pizza) => {
    return total + (pizza.prices.small * pizza.quantity); // You can change to medium/large
  }, 0);

  return (
    <div>
      <h1>ðŸ›’ Your Cart</h1>

      {cart.length === 0 ? (
        <p>No items in the cart.</p>
      ) : (
        <>
          {cart.map((pizza, index) => (
            <div
              key={index}
              style={{
                border: '1px solid #ccc',
                marginBottom: '1rem',
                padding: '1rem',
                borderRadius: '8px'
              }}
            >
              <h3>{pizza.name}</h3>
              <p>Quantity: {pizza.quantity}</p>
              <p>Price per Pizza (Small): â‚¹{pizza.prices.small}</p>
              <p>Subtotal: â‚¹{pizza.prices.small * pizza.quantity}</p>
              <button onClick={() => removeFromCart(pizza._id)}>Remove</button>
            </div>
          ))}

          <h3>Total Price: â‚¹{totalPrice}</h3>
        </>
      )}
    </div>
  );
}

export default Cart;
