// src/App.js
import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

// Pages
import Home from "./pages/Home";
import Cart from "./pages/CartPage";
import Login from "./pages/Login";
import Register from "./pages/Register";
import CustomPizza from "./pages/CustomPizza";
import Checkout from "./pages/Checkout"; // ‚úÖ added checkout page

// Components
import Navbar from "./components/Navbar";
import Chatbot from "./components/Chatbot";

function App() {
  const [cart, setCart] = useState([]);

  // üëâ Add normal pizza
  const addToCart = (pizza) => {
    setCart((prevCart) => {
      const existing = prevCart.find(
        (item) => item._id === pizza._id && !item.isCustom
      );
      if (existing) {
        toast.info(`${pizza.name} quantity updated üçï`);
        return prevCart.map((item) =>
          item._id === pizza._id && !item.isCustom
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      toast.success(`${pizza.name} added to cart üçï`);
      return [...prevCart, { ...pizza, quantity: 1, isCustom: false }];
    });
  };

  // üëâ Add custom pizza (fixed price = ‚Çπ400)
  const addCustomPizzaToCart = (customPizza) => {
    setCart((prevCart) => [
      ...prevCart,
      {
        ...customPizza,
        price: 400, // ‚úÖ set custom pizza price
        quantity: 1,
        isCustom: true,
        cartId: Date.now(),
      },
    ]);
    toast.success("Your custom pizza was added üçï‚ú®");
  };

  return (
    <Router>
      {/* Navbar */}
      <Navbar cart={cart} />

      {/* Main Content */}
      <div className="max-w-7xl mx-auto p-4">
        <Routes>
          <Route path="/" element={<Home addToCart={addToCart} />} />
          <Route path="/cart" element={<Cart cart={cart} setCart={setCart} />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route
            path="/custom-pizza"
            element={<CustomPizza addCustomPizzaToCart={addCustomPizzaToCart} />}
          />
          <Route path="/checkout" element={<Checkout cart={cart} setCart={setCart} />} /> 
          {/* ‚úÖ checkout route */}
        </Routes>
      </div>

      {/* Chatbot always visible */}
      <Chatbot />

      {/* Toast notifications container */}
      <ToastContainer position="top-right" autoClose={2000} />
    </Router>
  );
}

export default App;
