// src/pages/Home.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';
import Pizza from '../components/Pizza';
import Pagination from '../components/Pagination';
import { FaHome, FaShoppingCart, FaUser, FaUserPlus, FaPizzaSlice } from 'react-icons/fa';

export default function Home({ addToCart }) {
  const navigate = useNavigate();

  // Pizzas state
  const [pizzas, setPizzas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Filters & pagination
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('all');
  const [page, setPage] = useState(1);
  const [pages, setPages] = useState(1);

  useEffect(() => {
    let isMounted = true;

    const fetchPizzas = async () => {
      try {
        setLoading(true);
        const { data } = await api.get(
          `/pizzas?page=${page}&search=${searchTerm}&category=${category}`
        );
        if (isMounted) {
          setPizzas(data.pizzas);
          setPages(data.pages);
        }
      } catch (err) {
        if (isMounted) {
          console.error("Failed to fetch pizzas:", err);
          setError('Could not load pizzas. Please make sure the backend server is running.');
        }
      } finally {
        if (isMounted) setLoading(false);
      }
    };

    fetchPizzas();
    return () => { isMounted = false; };
  }, [page, searchTerm, category]);

  // Girlish menu cards
  const menuItems = [
    { title: "Home", icon: <FaHome size={40} />, path: "/", color: "bg-pink-300" },
    { title: "Cart", icon: <FaShoppingCart size={40} />, path: "/cart", color: "bg-yellow-300" },
    { title: "Login", icon: <FaUser size={40} />, path: "/login", color: "bg-purple-300" },
    { title: "Register", icon: <FaUserPlus size={40} />, path: "/register", color: "bg-green-300" },
    { title: "Custom Pizza", icon: <FaPizzaSlice size={40} />, path: "/custom-pizza", color: "bg-red-300" },
  ];

  const categories = ['all', 'veg', 'non-veg', 'classic', 'specialty'];

  const handleFilterChange = (setter) => (e) => {
    setter(e.target.value);
    setPage(1);
  };

  return (
    <div className="p-8">
      {/* Page Title */}
      <h1 className="text-4xl font-bold text-pink-500 mb-8 text-center">🍕 Pizza Paradise</h1>

      {/* Colorful Menu Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mb-10">
        {menuItems.map(item => (
          <div
            key={item.title}
            className={`flex flex-col items-center justify-center p-6 rounded-2xl shadow-lg transform transition duration-300 hover:scale-105 hover:shadow-xl cursor-pointer ${item.color}`}
            onClick={() => navigate(item.path)}
          >
            <div className="mb-4">{item.icon}</div>
            <div className="text-xl font-semibold">{item.title}</div>
          </div>
        ))}
      </div>

      {/* Search & Category Filters */}
      <div className="flex flex-wrap justify-center gap-6 mb-8">
        <div>
          <label htmlFor="search" className="mr-2 font-medium">Search Pizzas:</label>
          <input
            id="search"
            type="text"
            placeholder="e.g., Margherita"
            value={searchTerm}
            onChange={handleFilterChange(setSearchTerm)}
            className="p-2 rounded border border-gray-300"
          />
        </div>
        <div>
          <label htmlFor="category" className="mr-2 font-medium">Filter by Category:</label>
          <select
            id="category"
            value={category}
            onChange={handleFilterChange(setCategory)}
            className="p-2 rounded border border-gray-300"
          >
            {categories.map(cat => (
              <option key={cat} value={cat} className="capitalize">{cat}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Pizza Menu */}
      <h2 className="text-2xl font-bold text-center mb-6">Pizza Menu</h2>
      {loading ? (
        <div className="text-center">Loading menu...</div>
      ) : error ? (
        <div className="text-red-500 text-center p-4">{error}</div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 justify-center">
          {pizzas.length > 0 ? (
            pizzas.map(pizza => (
              <Pizza key={pizza._id} pizza={pizza} addToCart={addToCart} />
            ))
          ) : (
            <p className="text-center col-span-full">No pizzas match your criteria.</p>
          )}
        </div>
      )}

      <Pagination pages={pages} page={page} setPage={setPage} />
    </div>
  );
}
