import React from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import api from '../api';

export default function Checkout({ cart, user, setCart }) {
  const navigate = useNavigate();

  const totalPrice = cart.reduce((total, pizza) => {
    const price = pizza.price || 0;
    return total + (price * pizza.quantity);
  }, 0);

  const handlePlaceOrder = async () => {
    if (!user) {
      toast.error('You must be logged in to place an order.');
      navigate('/login');
      return;
    }

    if (cart.length === 0) {
      toast.error('Your cart is empty.');
      return;
    }

    const orderDetails = {
      userId: user.user.id,
      pizzas: cart.map((item) => ({
        pizzaId: !item.isCustom ? item._id : null,
        name: item.name,
        price: item.price,
        quantity: item.quantity,
        size: !item.isCustom ? item.size : undefined, // Add the size
        base: item.isCustom ? item.base : undefined, 
        sauce: item.isCustom ? item.sauce : undefined,
        isCustom: !!item.isCustom,
      })),
      totalAmount: totalPrice,
    };

    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`,
        },
      };

      await api.post("/orders", orderDetails, config);

      toast.success("Order placed successfully!");
      setCart([]); // Clear the cart
      navigate("/my-orders"); // Redirect to an order history page
    } catch (error) {
      toast.error('Failed to place order. Please try again.');
      console.error('Order placement error:', error);
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: 'auto' }}>
      <h1>Checkout</h1>
      <h2>Order Summary</h2>
      {cart.map((item, index) => (
        <div key={item.cartId || index} style={{ display: 'flex', justifyContent: 'space-between', borderBottom: '1px solid #eee', padding: '10px 0' }}>
          <span>{item.name} (x{item.quantity})</span>
          <span>₹{((item.price || 0) * item.quantity).toFixed(2)}</span>
        </div>
      ))}
      <h3 style={{ textAlign: 'right', marginTop: '1rem' }}>
        Total: ₹{totalPrice.toFixed(2)}
      </h3>
      <button
        onClick={handlePlaceOrder}
        style={{ marginTop: '1rem', width: '100%', padding: '15px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', fontSize: '16px' }}
      >
        Confirm and Place Order
      </button>
    </div>
  );
}
