// frontend/src/components/Cart.js
import React from 'react';
import { useNavigate } from 'react-router-dom';

function Cart({ cart, setCart }) {
  const removeFromCart = (cartId) => {
    const updatedCart = cart.filter(pizza => pizza.cartId !== cartId);
    setCart(updatedCart);
  };

  const navigate = useNavigate();

  const totalPrice = cart.reduce((total, pizza) => {
    // Each pizza in the cart now has its own price property based on the selected size
    return total + (pizza.price * pizza.quantity);
  }, 0);

  return (
    <div>
      <h1>ðŸ›’ Your Cart</h1>

      {cart.length === 0 ? (
        <p>No items in the cart.</p>
      ) : (
        <>
          {cart.map((pizza) => (
            <div
              key={pizza.cartId} // Use the unique cartId as the key
              style={{
                border: '1px solid #ccc',
                marginBottom: '1rem',
                padding: '1rem',
                borderRadius: '8px'
              }}
            >
              <h3>{pizza.name}</h3> {/* Name now includes size, e.g., "Margherita (Medium)" */}
              {pizza.isCustom && (
                <p>Base: {pizza.base}, Sauce: {pizza.sauce}</p>
              )}
              <p>Quantity: {pizza.quantity}</p>
              <p>
                Price per Pizza: â‚¹{pizza.price.toFixed(2)}
              </p>
              <p>Subtotal: â‚¹{(pizza.price * pizza.quantity).toFixed(2)}</p>
              <button onClick={() => removeFromCart(pizza.cartId)}>Remove</button>
            </div>
          ))}
          <h3>Total Price: â‚¹{totalPrice.toFixed(2)}</h3>
          <button
            onClick={() => navigate('/checkout')}
            style={{ marginTop: '1rem', padding: '10px 20px', backgroundColor: 'green', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
          >
            Proceed to Checkout
          </button>
        </>
      )}
    </div>
  );
}

export default Cart;
