import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css"; // ✅ Make sure styles are loaded

const pizzaBases = [
  "Thin Crust",
  "Cheese Burst",
  "Whole Wheat",
  "Gluten-Free",
  "Classic Hand Tossed"
];
const sauces = [
  "Tomato Basil",
  "Barbecue",
  "Alfredo",
  "Pesto",
  "Spicy Marinara"
];

export default function CustomPizza() {
  const [selectedBase, setSelectedBase] = useState("");
  const [selectedSauce, setSelectedSauce] = useState("");
  const navigate = useNavigate();

  const handleSubmit = () => {
    if (!selectedBase || !selectedSauce) {
      toast.error("Please choose a base and a sauce!");
      return;
    }

    // Here you could POST to your backend instead of console.log
    console.log("Custom pizza:", { selectedBase, selectedSauce });

    toast.success("Your custom pizza has been added to the cart!");
    navigate("/cart");
  };

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Build Your Custom Pizza</h1>

      {/* Base Selection */}
      <h2 className="text-lg font-semibold mt-4 mb-2">Choose a Base</h2>
      <div className="space-y-2">
        {pizzaBases.map((base) => (
          <label key={base} className="flex items-center space-x-2">
            <input
              type="radio"
              name="base"
              value={base}
              checked={selectedBase === base}
              onChange={(e) => setSelectedBase(e.target.value)}
            />
            <span>{base}</span>
          </label>
        ))}
      </div>

      {/* Sauce Selection */}
      <h2 className="text-lg font-semibold mt-4 mb-2">Choose a Sauce</h2>
      <div className="space-y-2">
        {sauces.map((sauce) => (
          <label key={sauce} className="flex items-center space-x-2">
            <input
              type="radio"
              name="sauce"
              value={sauce}
              checked={selectedSauce === sauce}
              onChange={(e) => setSelectedSauce(e.target.value)}
            />
            <span>{sauce}</span>
          </label>
        ))}
      </div>

      {/* Submit Button */}
      <button
        onClick={handleSubmit}
        className="mt-6 bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700"
      >
        Add to Cart
      </button>
    </div>
  );
}
