// src/pages/CartPage.js
import React from "react";
import { Link } from "react-router-dom";

export default function CartPage({ cart, setCart }) {
  const removeFromCart = (id, isCustom) => {
    setCart((prevCart) =>
      prevCart.filter((item) =>
        isCustom ? item.cartId !== id : item._id !== id
      )
    );
  };

  if (!cart || cart.length === 0) {
    return (
      <div style={{ textAlign: "center", marginTop: "2rem" }}>
        <h2>Your Cart ðŸ›’</h2>
        <p>No items in your cart yet.</p>
        <Link to="/">Go back to menu</Link>
      </div>
    );
  }

  const total = cart.reduce((sum, item) => {
    const price =
      item.isCustom ? 400 : Number(item.price) || 0; // âœ… custom pizza = 400
    return sum + price * (item.quantity || 1);
  }, 0);

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2>Your Cart ðŸ›’</h2>
      {cart.map((item) => {
        const price =
          item.isCustom ? 400 : Number(item.price) || 0; // âœ… custom pizza = 400
        const qty = item.quantity || 1;

        return (
          <div
            key={item.cartId || item._id}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              borderBottom: "1px solid #eee",
              padding: "10px 0",
            }}
          >
            <span>
              {item.name || "Custom Pizza"}{" "}
              {qty > 1 && `(x${qty})`} {/* âœ… no x1 */}
            </span>
            <span>â‚¹{(price * qty).toFixed(2)}</span>
            <button
              onClick={() =>
                removeFromCart(item.isCustom ? item.cartId : item._id, item.isCustom)
              }
              style={{
                background: "red",
                color: "white",
                border: "none",
                padding: "5px 10px",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              Remove
            </button>
          </div>
        );
      })}

      <h3 style={{ textAlign: "right", marginTop: "1rem" }}>
        Total: â‚¹{total.toFixed(2)}
      </h3>

      {/* Checkout button */}
      <div style={{ textAlign: "right", marginTop: "1rem" }}>
        <Link to="/checkout">
          <button
            style={{
              background: "green",
              color: "white",
              padding: "10px 20px",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Proceed to Checkout
          </button>
        </Link>
      </div>
    </div>
  );
}
